/**
 * Handle WASD, arrow keys.
 */
function handleKeys(ev) {
  switch (ev.keyCode) {
    case 37:  // left
    case 65:  // 'a'
      moveLeft();
      break;
    case 38:  // up
    case 87:  // 'w'
      moveUp();
      break;
    case 39:  // right
    case 68:  // 'd'
      moveRight();
      break;
    case 40:  // down
    case 83:  // 's'
      moveDown();
      break;
  }
};

